import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-match',
  templateUrl: './match.page.html',
  styleUrls: ['./match.page.scss'],
})
export class MatchPage implements OnInit {

  constructor() { }

  ngOnInit() {
  }
  players = [
    { name: 'Jugador 1' },
    { name: 'Jugador 2' },
    { name: 'Jugador 3' },
  ];
  sports = ['Fútbol', 'Básquetbol', 'Voleibol'];
  selectedPlayer = null;

  selectPlayer(player: any) {
    this.selectedPlayer = player;
  }
}
<ion-header>
  <ion-toolbar color="primary">
    <ion-title>Iniciar Enfrentamiento</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card *ngFor="let player of players">
    <ion-card-header>
      <ion-card-title>{{ player.name }}</ion-card-title>
    </ion-card-header>
    <ion-card-content>
      <ion-button (click)="selectPlayer(player)">Seleccionar</ion-button>
    </ion-card-content>
  </ion-card>

  <ion-list *ngIf="selectedPlayer">
    <ion-item *ngFor="let sport of sports">
      <ion-label>{{ sport }}</ion-label>
    </ion-item>
  </ion-list>
</ion-content>
